import StripeCheckout from 'react-stripe-checkout';
import styled from 'styled-components';
import { Button } from '@material-ui/core';

const StripeCheckoutButton = ({price})=>{
    const priceForStripe = price*100
    const publishableKey = "pk_test_51J0fEuEwf5qTr9qrXCYIEcazITtx0ZlJ5qInlTlHHD6x1wEfc6U9AZS2wcvCzYUNxOpKhXEZPl2ZnFO0aHHmq3jH00Qsywi0qU"
    const onToken =()=>{
        console.log('Pay successfully')
    }
    return (
        <StripeCheckout
           label = {`Buy: ${price},000 đ`}
           name = 'Google Play'
           billingAddress
           shippingAddress
           data-label ="https://scontent.fsgn2-5.fna.fbcdn.net/v/t1.15752-9/s2048x2048/228085913_714575273276171_6064261169318172546_n.png?_nc_cat=102&ccb=1-4&_nc_sid=ae9488&_nc_ohc=jdbXs-KAlFQAX8tGhVZ&_nc_ht=scontent.fsgn2-5.fna&oh=0a8266e782334f2fe635a3dc4e6bc836&oe=613B4933"
           currency="VND"
           image="https://scontent.fsgn2-5.fna.fbcdn.net/v/t1.15752-9/s2048x2048/228085913_714575273276171_6064261169318172546_n.png?_nc_cat=102&ccb=1-4&_nc_sid=ae9488&_nc_ohc=jdbXs-KAlFQAX8tGhVZ&_nc_ht=scontent.fsgn2-5.fna&oh=0a8266e782334f2fe635a3dc4e6bc836&oe=613B4933"
           description={`Your total is ${price}.000 VND`}
           amount = {priceForStripe}
           panelLabel = 'Pay now'
           token = {onToken}
           allowRememberMe 
           class=""
           stripeKey={publishableKey}            
        />
    )
}

const BuyButton = styled(Button)`
    background-color:#ed3b3b !important;
    color:white !important;
    font-weight: bold !important;
    box-sizing:border-box;
    position: relative;
    padding:5px 10px !important;
    box-shadow: rgba(0, 0, 0, 0.4) 0px 2px 4px, rgba(0, 0, 0, 0.3) 0px 7px 13px -3px, rgba(0, 0, 0, 0.2) 0px -3px 0px inset;
    text-transform: capitalize !important;
    transition: all 250ms cubic-bezier(0.25, 0.46, 0.45,0.94) 0s !important; 


    span{
        /* background-color:#ed3b3b !important;
        color:white !important; */
        button{
            position:absolute;
            span{
                background-color: red!important;
                color:black !important
            }
        }
    }
`
const Container = styled.div`

`

export default StripeCheckoutButton
