import StripeCheckout from 'react-stripe-checkout';


const StripeCheckoutButton = ({price})=>{
    const priceForStripe = price*100
    const publishableKey = "pk_test_51J0fEuEwf5qTr9qrXCYIEcazITtx0ZlJ5qInlTlHHD6x1wEfc6U9AZS2wcvCzYUNxOpKhXEZPl2ZnFO0aHHmq3jH00Qsywi0qU"
    const onToken =()=>{
        console.log('Pay successfully')
    }
    return (
        <StripeCheckout
            label = {`Buy: ${price}`}
            name = 'Google Play'
            billingAddress
            shippingAddress
            image="https://scontent.fsgn2-5.fna.fbcdn.net/v/t1.15752-9/s2048x2048/228085913_714575273276171_6064261169318172546_n.png?_nc_cat=102&ccb=1-4&_nc_sid=ae9488&_nc_ohc=jdbXs-KAlFQAX8tGhVZ&_nc_ht=scontent.fsgn2-5.fna&oh=0a8266e782334f2fe635a3dc4e6bc836&oe=613B4933"
            description={`Your total is $${price}`}
            amount = {priceForStripe}
            panelLabel = 'Pay now'
            token = {onToken}
            allowRememberMe 
            stripeKey={publishableKey}            
        />
    )
}

export default StripeCheckoutButton
